package org.troy.manage.action;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.troy.core.action.GenericAction;import org.troy.manage.entity.Test;import org.troy.manage.service.TestService;/** * Created by IntelliJ IDEA.<p/> * User: troy-kou<p/> * Date: 13-8-7<p/> * Time: 下午5:38<p/> * Email:kouhongyu@163.com<p/> */@Controllerpublic class TestAction extends GenericAction {    public String test() throws Exception {        return SUCCESS;    }    public String testInsert(){       try{        Test test = new Test();        test.setTestname("tt_name");        testService.insertTest(test);           System.out.println("test = " + test);           putJson("test",test);       }catch (Exception e){           e.printStackTrace();           putStatus(e.getMessage());       }        return SUCCESS;    }    @Autowired    private TestService testService;    private String testInfo;    public String getTestInfo() {        return testInfo;    }    public void setTestInfo(String testInfo) {        this.testInfo = testInfo;    }}